#靠交31219
資工FSM

always@(posedge clk)begin
    if (人生重來槍) P <= S_出生;
    else P <= P_next;
end

always@(*)begin
    case(P)
        S_出生:
            P_next <= S_上學;
        S_上學:
            if (考上資工) P_next <= S_讀資工;
            else P_next <= S_上學;
        S_讀資工:
            P_next <= S_寫作業;
        S_寫作業:
            if (放棄) P_next <= S_賣雞排;
            else if (寫完 && !放棄) P_next <= S_畢業;
            else P_next <= S_寫作業;
        S_畢業:
            P_next <= S_工作;
        S_工作:
            if (不想輪班)P_next <= S_賣雞排;
            esle P_next <= S_工作;
        S_賣雞排:
            P_next <= S_賣雞排;
        S_交女友:               /*state reduce*/
            P_next <= S_溫拿;
        S_溫拿:
            P_next <= S_賣雞排;
        default:
            P_next <= P_next;
    endcase
end
